import React, {Component} from 'react';
import {Card, Form, Button} from 'react-bootstrap';

export default class AddCountryNumber extends Component{

    constructor(props) {
            super(props);
            this.state = {
                countryNumbers: []
            };
            this.numberChange = this.numberChange.bind(this);
            this.submitNumber = this.submitNumber.bind(this);
    }

    submitNumber(event){
        alert(this.state.name);
        event.preventDefault();
    }

    numberChange(event){
        this.setState({
            [event.target.name]:event.target.value
        });
    }

    render(){
        return(
            <Card className="border border-dark bg-dark text-white">
                 <Card.Header>Add Number</Card.Header>
                 <Card.Body>
                    <Form onSubmit={this.submitNumber} id="numberFormId">
                      <Form.Group controlId="formGridTitle">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control required type="name" placeholder="Enter Name" value={this.state.name} onChange={this.nameChange} />
                      </Form.Group>

                      <Form.Group >
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="Phone Number" placeholder=" Enter Phone Number" />
                      </Form.Group>
                      <Button variant="primary" type="submit">
                        Submit
                      </Button>
                    </Form>
                 </Card.Body>
            </Card>
        );
    }

}
