import React, {Component} from 'react';
import {Card, Form, Button} from 'react-bootstrap';

import axios from 'axios';

export default class AddCountryNumber extends Component{

    constructor(props) {
            super(props);
            this.state = this.initialState;
            this.numberChange = this.numberChange.bind(this);
            this.submitNumber = this.submitNumber.bind(this);
    }

    initialState = {name, phone}

    resetNumber = () => {
        this.setState(() => this.initialState);
    }

    submitNumber(event){
        event.preventDefault();

        const book = {
            name: this.state.name,
            phone:this.state.phone
        };

        axios.post("http://localhost:8080/numbers/", book)
            .then(response => {
                if(response.data != null){
                    this.setState(this.initialState);
                    alert("Number saved successfully")
                }
            })

    }

    numberChange(event){
        this.setState({
            [event.target.name]:event.target.value
        });
    }

    render(){
        const {name,phone} = this.state;

        return(
            <Card className="border border-dark bg-dark text-white">
                 <Card.Header>Add Number</Card.Header>
                    <Form onReset={this.resetNumber} onSubmit={this.submitNumber} id="numberFormId">
                    <Card.Body>
                      <Form.Group controlId="formGridName">
                        <Form.Label>Name</Form.Label>
                        <Form.Control required autoComplete="off" type="text" placeholder="Enter Name" name="name" value={name} onChange={this.nameChange} className={"bg-dark text-white"}/>
                      </Form.Group>

                      <Form.Group  controlId="formGridPhone">
                        <Form.Label>Number</Form.Label>
                        <Form.Control required autoComplete="off" name="phone" type="text" placeholder=" Enter Phone Number" value={phone} onChange={this.phoneChange} className={"bg-dark text-white"}/>
                      </Form.Group>
                      </Card.Body>
                    </Form>
                 <Card.Footer style={{"textAlign":"right"}}>
                     <Button size="sm" variant="success" type="submit">
                       Submit
                     </Button>
                     <Button size="sm" variant="info" type="reset">
                       Reset
                     </Button>
                 </Card.Footer>
            </Card>
        );
    }

}
