import React, {Component} from 'react';
import {Card, Form, Button} from 'react-bootstrap';

export default class AddCountryNumber extends Component{

    constructor(props) {
            super(props);
            this.state = {
                countryNumbers: []
            };
            this.numberChange = this.numberChange.bind(this);
            this.submitNumber = this.submitNumber.bind(this);
    }

    submitNumber(event){
        alert('Name: '+this.state.name+', Phone: '+this.state.phone);
        event.preventDefault();
    }

    numberChange(event){
        this.setState({
            [event.target.name]:event.target.value
        });
    }

    render(){
        const {name,phone} = this.state;

        return(
            <Card className="border border-dark bg-dark text-white">
                 <Card.Header>Add Number</Card.Header>
                 <Card.Body>
                    <Form onSubmit={this.submitNumber} id="numberFormId">
                      <Form.Group controlId="formGridName">
                        <Form.Label>Name</Form.Label>
                        <Form.Control required type="text" placeholder="Enter Name" value={name} onChange={this.nameChange} />
                      </Form.Group>

                      <Form.Group  controlId="formGridPhone">
                        <Form.Label>Number</Form.Label>
                        <Form.Control required type="text" placeholder=" Enter Phone Number" value={phone} onChange={this.phoneChange}/>
                      </Form.Group>
                      <Button variant="primary" type="submit">
                        Submit
                      </Button>
                    </Form>
                 </Card.Body>
            </Card>
        );
    }

}
