import React, {Component} from 'react';
import {Card, Form, Button} from 'react-bootstrap';

export default class AddCountryNumber extends Component{

    constructor(props) {
            super(props);
            this.state = {
                countryNumbers: []
            }
    }

    render(){
        return(
            <Card className="border border-dark bg-dark text-white">
                 <Card.Header>Add Number</Card.Header>
                 <Card.Body>
                    <Form onSubmite={this.submiteNumber} id="numberFormId">
                      <Form.Group className="mb-3" controlId="formBasicEmail">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control required type="Name" placeholder="Enter Name" value={this.state.name} onChange={this.nameChange} />
                      </Form.Group>

                      <Form.Group className="mb-3" controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="Phone Number" placeholder=" Enter Phone Number" />
                      </Form.Group>
                      <Button variant="primary" type="submit">
                        Submit
                      </Button>
                    </Form>
                 </Card.Body>
            </Card>
        );
    }

}
